(14, 1, '2020-05-02 23:11:36', '2020-05-02 15:11:36', '/**\r\n* Disable WP 4.2 emoji\r\n*/\r\nfunction ace_remove_emoji() {\r\n	add_filter( \'emoji_svg_url\', \'__return_false\' );\r\n	remove_action( \'admin_print_styles\', \'print_emoji_styles\' );\r\n	remove_action( \'wp_head\', \'print_emoji_detection_script\', 7 );\r\n	remove_action( \'admin_print_scripts\', \'print_emoji_detection_script\' );\r\n	remove_action( \'wp_print_styles\', \'print_emoji_styles\' );\r\n	remove_filter( \'wp_mail\', \'wp_staticize_emoji_for_email\' );\r\n	remove_filter( \'the_content_feed\', \'wp_staticize_emoji\' );\r\n	remove_filter( \'comment_text_rss\', \'wp_staticize_emoji\' );\r\n	// filter to remove TinyMCE emojis\r\n	add_filter( \'tiny_mce_plugins\', \'ace_disable_emoji_tinymce\' );\r\n}\r\nadd_action( \'init\', \'ace_remove_emoji\' );\r\n/**\r\n* Remove tinyMCE emoji\r\n*/\r\nfunction ace_disable_emoji_tinymce( $plugins ) {\r\n	unset( $plugins[\'wpemoji\'] );\r\n	return $plugins;\r\n}\r\n', 'Simple php snippet: Disable emojis', '', 'publish', 'closed', 'closed', '', 'simple-php-snippet-disable-emojis', '', '', '2020-05-02 23:11:36', '2020-05-02 15:11:36', '', 0, 'http://127.0.0.1/2020/05/02/simple-php-snippet-disable-emojis/', 0, 'wbcr-snippets', '', 0),
